import { dirname, join } from "node:path";
import AutoLoad, { type AutoloadPluginOptions } from "@fastify/autoload";
import type { FastifyPluginAsync, FastifyServerOptions } from "fastify";
// import { fileURLToPath } from "node:url";
import { fileURLToPath } from "node:url";

import "dotenv/config";
import { tireSchemas } from "./schemas/tires.schema";
// Register JSON Schemas generated by fastify-zod before routes are loaded
// import { userSchemas } from "@/schemas/auth";
// import { eventSchemas } from "@/schemas/event";

import fastifyWebsocket from "@fastify/websocket";

export interface AppOptions
  extends FastifyServerOptions,
    Partial<AutoloadPluginOptions> {}

const options: AppOptions = {};

const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);

const app: FastifyPluginAsync<AppOptions> = async (
  fastify,
  opts
): Promise<void> => {
  // Add schemas so $ref() in route schemas can be resolved by Fastify
  for (const schema of [...tireSchemas]) {
    fastify.addSchema(schema);
  }

  // await fastify.register(import("@fastify/swagger"), {
  //   openapi: {
  //     openapi: "3.0.0",
  //     info: {
  //       title: "Event Scheduler API",
  //       description: "API documentation for the Event Scheduler application",
  //       version: "1.0.0",
  //     },
  //     servers: [
  //       {
  //         url: `http://localhost:${process.env.PORT || 3000}`,
  //         description: "Development Server",
  //       },
  //     ],
  //     externalDocs: {
  //       url: "https://swagger.io",
  //       description: "Find more info here",
  //     },
  //   },
  // });

  // fastify.register(import("@fastify/swagger-ui"), {
  //   routePrefix: "/docs",
  //   uiConfig: {
  //     docExpansion: "full",
  //     deepLinking: false,
  //   },
  // });

  // Place here your custom code!
  fastify.setErrorHandler((error: Error, request, reply) => {
    // Prefer Fastify's logger if present, but always log something.
    if (request.log) request.log.error({ err: error }, "Unhandled error");
    else console.error(error);

    if (reply.sent) return;
    const statusCode = (error as any)?.statusCode ?? 500;
    reply.status(statusCode).send({
      error: "Internal Server Error",
      message: statusCode < 500 ? error.message : "Unexpected error",
    });
  });
  // Do not touch the following lines

  // This loads all plugins defined in plugins
  // those should be support plugins that are reused
  // through your application

  // Import websocket plugin
  await fastify.register(fastifyWebsocket);

  await fastify.register(AutoLoad, {
    dir: join(__dirname, "plugins"),
    options: opts,
  });

  // This loads all plugins defined in routes
  // define your routes in one of these

  await fastify.register(AutoLoad, {
    dir: join(__dirname, "routes"),
    options: opts,
  });
};

export default app;
export { app, options };
